mapper.select:
SELECT * FROM business_quarterly 
WHERE year = #{year} 
AND quarter = #{quarter} 
AND business_type = #{businessType}
AND is_deleted = 0

mapper.update:
UPDATE business_quarterly 
SET amount = amount + #{amount}, 
    updated_at = CURRENT_TIMESTAMP, 
    version = version + 1
WHERE year = #{year} 
AND quarter = #{quarter} 
AND business_type = #{businessType}
AND is_deleted = 0
